const WYRE_ACCOUNT_ID = "AC_NLVGLA2DCCD"

type WyreService struct {
	apiKey   string
	endpoint string
}

func NewWyreService(apiKey, endpoint string) *WyreService {
	return &WyreService{
		apiKey:   apiKey,
		endpoint: endpoint,
	}
}

func NewWyreServiceWithENV() *WyreService {
	return NewWyreService(os.Getenv("WYRE_API_KEY"), os.Getenv("WYRE_API_ENDPOINT"))
}

func (svc *WyreService) Ping(ctx context.Context) error {
	return nil
}

