func (svc *WyreService) RequestWyrePreDeposit(ctx context.Context, request *service.RequestWyreDepositRequest) (*service.PreReserveResponse, error) {
	return svc.preReserve(
		request.WalletAddress,
		request.Amount,
		request.SourceCurrency,
		request.DestCurrency,
		request.NetworkId,
	)
}

func (svc *WyreService) RequestWyreDeposit(
	ctx context.Context,
	request *service.RequestWyreDepositRequest,
) (string, error) {
	return svc.reserve(
		request.WalletAddress,
		request.Amount,
		request.SourceCurrency,
		request.DestCurrency,
		request.NetworkId,
	)
}

func (svc *WyreService) reserve(
	walletAddress string,
	amount uint,
	sourceCurrency string,
	destCurrency string,
	network string,
) (string, error) {
	url := fmt.Sprintf("%s/v3/orders/reserve", svc.endpoint)

	if !strings.Contains("USD, GBP, EUR, CAD, AUD", sourceCurrency) {
		return "", fmt.Errorf("unsupported source currency: %s", sourceCurrency)
	}

	payloadobj := map[string]interface{}{
		"country":           "US",
		"sourceAmount":      fmt.Sprintf("%d", amount),
		"sourceCurrency":    sourceCurrency,
		"destCurrency":      destCurrency,
		"amountIncludeFees": true,
		"paymentMethod":     "debit-card",
		"dest":              fmt.Sprintf("%s:%s", network, walletAddress),
		"referrerAccountId": WYRE_ACCOUNT_ID,
	}
	bytes, err := json.Marshal(payloadobj)

	if err != nil {
		return "", err
	}

	payload := strings.NewReader(string(bytes))

	req, _ := http.NewRequest("POST", url, payload)

	req.Header.Add("Accept", "application/json")
	req.Header.Add("Content-Type", "application/json")
	req.Header.Add("Authorization", fmt.Sprintf("Bearer %s", svc.apiKey))

	res, err := http.DefaultClient.Do(req)

	if err != nil {
		return "", err
	}

	defer res.Body.Close()
	body, _ := ioutil.ReadAll(res.Body)

	if res.StatusCode != 200 {
		var reserveResponse map[string]interface{}
		if err := json.Unmarshal(body, &reserveResponse); err != nil {
			return "", err
		}
		return "", fmt.Errorf("%s", reserveResponse["message"])
	}

	var reserveResponse map[string]string
	if err := json.Unmarshal(body, &reserveResponse); err != nil {
		return "", err
	}

	return reserveResponse["url"], nil
}

