func (svc *WyreService) preReserve(
	walletAddress string,
	amount uint,
	sourceCurrency string,
	destCurrency string,
	network string,
) (*service.PreReserveResponse, error) {
	url := fmt.Sprintf("%s/v3/orders/quote/partner", svc.endpoint)

	payloadobj := map[string]interface{}{
		"walletType":        "DEBIT_CARD",
		"sourceCurrency":    sourceCurrency,
		"destCurrency":      destCurrency,
		"country":           "US",
		"sourceAmount":      fmt.Sprintf("%d", amount),
		"amountIncludeFees": true,
		"dest":              fmt.Sprintf("%s:%s", network, walletAddress),
		"accountId":         WYRE_ACCOUNT_ID,
	}
	bytes, err := json.Marshal(payloadobj)

	if err != nil {
		return nil, err
	}

	payload := strings.NewReader(string(bytes))
	req, _ := http.NewRequest("POST", url, payload)
	req.Header.Add("Accept", "application/json")
	req.Header.Add("Content-Type", "application/json")
	req.Header.Add("Authorization", fmt.Sprintf("Bearer %s", svc.apiKey))

	res, err := http.DefaultClient.Do(req)

	if err != nil {
		return nil, err
	}

	defer res.Body.Close()
	body, _ := ioutil.ReadAll(res.Body)

	if res.StatusCode != 200 {
		var reserveResponse map[string]interface{}
		if err := json.Unmarshal(body, &reserveResponse); err != nil {
			return nil, err
		}
		return nil, fmt.Errorf("%s", reserveResponse["message"])
	}

	var reserveResponse service.PreReserveResponse
	if err := json.Unmarshal(body, &reserveResponse); err != nil {
		return nil, err
	}

	return &reserveResponse, nil
}
